@tailwind base;
@tailwind components;
@tailwind utilities;

import { createRoot } from "react-dom/client";
import { App } from "@siocode/base";

function AppContent() {
    return <>
        <div className="w-full h-full flex flex-col justify-center items-center">
            <h1 className="text-4xl font-bold">Brainstorm</h1>
            <p className="text-sm text-gray-500">This app is not completed yet.</p>
        </div>
    </>;
}

const root = createRoot(document.getElementById("root"));
root.render(
    <App headerMiddle={"Version 0.0.1"}>
        <AppContent />
    </App>
);

/** @type {import('tailwindcss').Config} */
module.exports = {
    content: [
        "public/**/*.html",
        "src/**/*.{js,jsx,ts,tsx}",
        "../base/src/**/*.{js,jsx,ts,tsx}",
    ],
    theme: {
        extend: {
            colors: {
                sioblue: "#002266",
            },
        },
    },
    plugins: [],
};

const { zip } = require("zip-a-folder");
const path = require("path");

console.log(`Creating zip for brainstorm ...`);
zip(path.join(__dirname, "./public"), path.join(__dirname, "./brainstorm.zip"));
console.log(`Zip created for brainstorm`);

